showInPopup=(n,t)=>{$.ajax({type:"GET",url:n,success:function(n){$("#form-modal .modal-body").html(n);$("#form-modal .modal-title").html(t);$("#form-modal").modal("show")},error:function(n){console.log(n.responseText)}})};ajaxPostBook=n=>{try{$.ajax({type:"POST",url:n.action,data:new FormData(n),contentType:!1,processData:!1,success:function(n){n==="Thêm thành công!!"?window.location.reload():$("#form-modal .modal-body").html(n)},error:function(){}})}catch(t){console.log(t)}return!1};ajaxDeleteBook=(n,t)=>{const i=n.action.substring(n.action.lastIndexOf("=")+1);if(confirm(`bạn có mún xóa ${t} có id =${i} không ?`))try{$.ajax({type:"POST",url:n.action,data:new FormData(n),contentType:!1,processData:!1,success:function(n){alert(n);window.location.reload()},error:function(n){alert(n.responseText)}})}catch(r){console.log(r)}return!1};