ajaxDeleteReview=n=>{const i=n.action.substring(n.action.lastIndexOf("=")+1);if(confirm(`bạn có mún xóa bình luận này không ?`))try{$.ajax({type:"POST",url:n.action,data:new FormData(n),contentType:!1,processData:!1,success:function(n){alert(n);window.location.reload()},error:function(){}})}catch(t){console.log(t)}return!1};