showInPopup=(n,t)=>{$.ajax({type:"GET",url:n,success:function(n){$("#form-modal .modal-body").html(n);$("#form-modal .modal-title").html(t);$("#form-modal").modal("show")},error:function(n){console.log(n.responseText)}})};ajaxPostUser=n=>{try{$.ajax({type:"POST",url:n.action,data:new FormData(n),contentType:!1,processData:!1,success:function(n){n==="Thêm user thành công!!"?window.location.reload():$("#form-modal .modal-body").html(n)},error:function(n){console.log(n.responseText)}})}catch(t){console.log(t)}return!1};ajaxDeleteUser=n=>{const t=n.action.substring(n.action.lastIndexOf("=")+1);if(confirm(`bạn có mún xóa tài khoản có id =${t} không ?`))try{$.ajax({type:"POST",url:n.action,data:new FormData(n),contentType:!1,processData:!1,success:function(n){n==="Xóa tài khoản thành công!!"?window.location.reload():alert(n)},error:function(){}})}catch(i){console.log(i)}return!1};